package song.api.attachment.controller;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import song.common.net.http.HttpFileResponse;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * description: * author:          song * createDate:      2018/1/24 */@RestController@RequestMapping("/downloader")public class DownloadController {    protected final static String filePathKey = "filePath";    protected final static String downloadFileNameKey = "downloadFileName";    @GetMapping(value = "server")    public void downloadServer(HttpServletRequest request, HttpServletResponse response) throws IOException {        HttpFileResponse fileResponse = new HttpFileResponse(response);        String filePath = request.getParameter(filePathKey);        String downloadName = request.getParameter(downloadFileNameKey);        fileResponse.localFile(filePath, downloadName);    }    @GetMapping(value = "ftp")    public void downloadFtp(HttpServletRequest request, HttpServletResponse response) throws IOException {        String filePath = request.getParameter(filePathKey);        String downloadName = request.getParameter(downloadFileNameKey);    }}