package song.api.common.base;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.CorsRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;import song.api.common.data.PagedData;import song.api.common.result.GridResult;/** * description: * author:          song * createDate:      2018/4/13 */@Configurationpublic class BaseController extends WebMvcConfigurerAdapter  {    @Override    public void addCorsMappings(CorsRegistry registry) {        registry.addMapping("/**")                .allowedOrigins("*")                .allowedMethods("GET", "POST", "PUT", "OPTIONS", "DELETE", "PATCH")                .allowCredentials(true).maxAge(3600);    }    public <T> GridResult<T> getGridResult(PagedData<T> pagedData) {        return new GridResult<T>(pagedData.getTotal(),pagedData.getData());    }}