package song.api.spider.common;/** * description: * author:          song * createDate:      2017/11/12 */public class CrawlerMovie {    private String id;    private String url;    private String title;    private String img;    private String downloadUrl;    private String performer;    public CrawlerMovie() {    }    public CrawlerMovie(String id, String url, String title, String img) {        this.id = id;        this.url = url;        this.title = title;        this.img = img;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    @Override    public String toString() {        return "{" +                "id='" + id + '\'' +                ", url='" + url + '\'' +                ", title='" + title + '\'' +                ", img='" + img + '\'' +                ", downloadUrl='" + downloadUrl + '\'' +                ", performer='" + performer + '\'' +                '}';    }    public String getPerformer() {        return performer;    }    public void setPerformer(String performer) {        this.performer = performer;    }    public String getDownloadUrl() {        return downloadUrl;    }    public void setDownloadUrl(String downloadUrl) {        this.downloadUrl = downloadUrl;    }    public String getUrl() {        return url;    }    public void setUrl(String url) {        this.url = url;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getImg() {        return img;    }    public void setImg(String img) {        this.img = img;    }}