package song.api.spider.common;import org.jsoup.nodes.Element;import org.jsoup.select.Elements;import song.common.lang.StringHelper;/** * description: * author:          song * createDate:      2017/10/17 */public class JsoupHelper {    public static boolean isEmpty(Elements elements) {        if (elements == null || elements.size() == 0) {            return true;        }        return false;    }    public static String attr(Element element, String attrKey) {        if (element != null) {            return element.attr(attrKey);        }        return StringHelper.getEmpty();    }    public static String text(Element element) {        if (element != null) {            return element.text();        }        return StringHelper.getEmpty();    }    public static String textFirst(Elements elements) {        if (!isEmpty(elements)) {            return elements.first().text();        }        return StringHelper.getEmpty();    }    public static String attrFirst(Elements elements, String attrKey) {        if (!isEmpty(elements)) {            return elements.first().attr(attrKey);        }        return StringHelper.getEmpty();    }}