package song.api.studio.builder.metadata;import song.common.db.DBConnectionConfig;import song.common.db.DBType;/** * description: * author:          song * createDate:      2017/10/25 */public class DbMetadataFactory {    /**     * 根据数据库类型和连接字符串获取数据库结构读取器     *     * @param config     * @return     * @throws Exception     */    public static DbMetadataReader create(DBConnectionConfig config) throws Exception {        DBType dbType = config.getDbType();        switch (config.getDbType()) {            case oracle:                return new OracleMetadataReader(config);            case mysql:                return new MySQLMetadataReader(config);            case sqlserver:                return new SQLServerMetadataReader(config);        }        throw new Exception(dbType + " database is not supported");    }}