package song.api.studio.controller;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import song.common.net.http.HttpFileResponse;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * description: * author:          song * createDate:      2018/1/24 */@Controller@RequestMapping("/downloader")public class DownloadController {    @RequestMapping(value = "download", method = RequestMethod.POST)    public void download(HttpServletRequest request, HttpServletResponse response) throws IOException {        HttpFileResponse fileResponse = new HttpFileResponse(response);        String localPath = request.getParameter("localPath");        String downloadName = request.getParameter("downloadFileName");        fileResponse.localFile(localPath, downloadName);    }}