package song.api.studio.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import song.api.studio.model.Project;import song.api.studio.service.IProjectService;import song.common.toolkit.base.BaseController;import java.util.List;/** * description: * author:          song * createDate:      2017/12/28 */@RequestMapping("/project")@Controllerpublic class ProjectController extends BaseController {    @Autowired    private IProjectService projectService;    @RequestMapping(value = "/getlist", method = RequestMethod.POST)    @ResponseBody    public List<Project> getList() {        return projectService.getList();    }}