package song.api.studio.model;import song.common.db.DBType;import song.common.toolkit.base.IdModel;import javax.persistence.Table;/** * description: * author:          song * createDate:      2017/10/25 */@Table(name = "studio_connection")public class Connection extends IdModel<String> {    private String connectionName;    private DBType dbType;    private String url;    private String userName;    private String password;    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getConnectionName() {        return connectionName;    }    public void setConnectionName(String connectionName) {        this.connectionName = connectionName;    }    public DBType getDbType() {        return dbType;    }    public void setDbType(DBType dbType) {        this.dbType = dbType;    }    public String getUrl() {        return url;    }    public void setUrl(String url) {        this.url = url;    }}