package song.api.studio.model;import song.common.toolkit.base.BaseModel;import song.common.toolkit.base.IdModel;import javax.persistence.Entity;import javax.persistence.Table;import javax.persistence.Transient;import java.util.ArrayList;import java.util.List;/** * description: * author:          song * createDate:      2017/10/25 */@Entity@Table(name = "studio_Project")public class Project extends IdModel<String> {    @javax.persistence.Column(name = "ProjectCode")    private String projectCode;    @javax.persistence.Column(name = "ProjectName")    private String projectName;    @javax.persistence.Column(name = "NameSpace")    private String nameSpace;    private String comment;    @javax.persistence.Column(name = "ConnectionId")    private String connectionId;    @javax.persistence.Column(name = "TemplateId")    private String templateId;    @Transient    private List<Table> tables = new ArrayList<Table>();    public List<Table> getTables() {        return tables;    }    public void setTables(List<Table> tables) {        this.tables = tables;    }    public String getProjectCode() {        return projectCode;    }    public void setProjectCode(String projectCode) {        this.projectCode = projectCode;    }    public String getProjectName() {        return projectName;    }    public void setProjectName(String projectName) {        this.projectName = projectName;    }    public String getNameSpace() {        return nameSpace;    }    public void setNameSpace(String nameSpace) {        this.nameSpace = nameSpace;    }    public String getComment() {        return comment;    }    public void setComment(String comment) {        this.comment = comment;    }    public String getConnectionId() {        return connectionId;    }    public void setConnectionId(String connectionId) {        this.connectionId = connectionId;    }    public String getTemplateId() {        return templateId;    }    public void setTemplateId(String templateId) {        this.templateId = templateId;    }}