package song.api.system.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import song.api.system.model.Dict;import song.api.system.service.IDictService;import song.common.result.ActionResult;import song.common.toolkit.base.BaseController;import java.util.List;/** * description: * author:          song * createDate:      2017/12/28 */@RequestMapping("/sysconfig")@RestControllerpublic class SysConfigController extends BaseController {    @Autowired    private IDictService dictService;    @GetMapping("/list")    public ActionResult getDictList(String dictType) {        List<Dict> data = dictService.getDictList(dictType);        return new ActionResult(data);    }}