package song.api.user.config;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Configuration;import org.springframework.web.method.support.HandlerMethodArgumentResolver;import org.springframework.web.servlet.config.annotation.CorsRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurationSupport;import song.api.user.common.CurrentUserIdMethodArgumentResolver;import song.common.toolkit.http.CorsHelper;import java.util.List;/** * description: * author:          song * createDate:      2018/5/24 */@Configurationpublic class ApplicationConfig extends WebMvcConfigurationSupport {    @Autowired    private CurrentUserIdMethodArgumentResolver currentUserIdMethodArgumentResolver;    @Override    public void addArgumentResolvers(List<HandlerMethodArgumentResolver> argumentResolvers) {        super.addArgumentResolvers(argumentResolvers);        argumentResolvers.add(currentUserIdMethodArgumentResolver);    }}