package song.api.user.config;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import song.api.common.config.SwaggerConfig;import springfox.documentation.spring.web.plugins.Docket;import springfox.documentation.swagger2.annotations.EnableSwagger2;/** * description: * author:          song * createDate:      2018/4/16 */@Configuration@EnableSwagger2public class Swagger2Config extends SwaggerConfig {    @Bean    public Docket createRestApi() {        return this.getDocket("song.api.user.controller");    }}