package song.api.user.config;import org.mybatis.spring.boot.autoconfigure.MybatisAutoConfiguration;import org.springframework.boot.autoconfigure.AutoConfigureAfter;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import song.api.common.helper.MapperHelper;import tk.mybatis.spring.mapper.MapperScannerConfigurer;/** * description: * author:          song * createDate:      2018/4/17 */@Configuration@AutoConfigureAfter(MybatisAutoConfiguration.class)public class TKMapperConfig {    @Bean    public MapperScannerConfigurer mapperScannerConfigurer() {        return MapperHelper.getTKMapperConfigurer("song.api.user.dao");    }}