package song.api.user.model;import song.common.toolkit.base.IdModel;import java.util.ArrayList;import java.util.Date;import java.util.List;/** * description: * author:          song * createDate:      2018/4/13 */public class User extends IdModel {    private String userId;    private String userName;    private String realName;    private Date birthday;    private List<String> roles;    public String getUserId() {        return userId;    }    public void setUserId(String userId) {        this.userId = userId;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getRealName() {        return realName;    }    public void setRealName(String realName) {        this.realName = realName;    }    public Date getBirthday() {        return birthday;    }    public void setBirthday(Date birthday) {        this.birthday = birthday;    }    public List<String> getRoles() {        return roles;    }    public void setRoles(List<String> roles) {        this.roles = roles;    }    public void addRole(String role){        if(this.roles==null){            roles = new ArrayList<String>();        }        roles.add(role);    }}