package song.common.toolkit.base;import song.common.result.ActionResult;import song.common.result.GridResult;import song.common.result.ResultCode;import song.common.toolkit.db.pager.PagedData;/** * description: * author:          song * createDate:      2018/4/13 */public class BaseController {    public <T> GridResult<T> gridSuccess(PagedData<T> pagedData) {        return new GridResult<T>(pagedData.getTotal(), pagedData.getData());    }    public ActionResult saveSuccess(boolean result) {        return new ActionResult(result, result ? "保存成功" : "保存失败");    }    public ActionResult success(Object data){        return new ActionResult(data);    }    public ActionResult success(){        return new ActionResult(true);    }    public ActionResult fail(String code,String msg,Object data){        return new ActionResult(false, code, msg, data);    }    public ActionResult fail(String msg,Object data) {        return fail(ResultCode.error, msg, data);    }    public ActionResult fail(String msg) {        return fail(msg, null);    }    public ActionResult unauthorized(String msg,Object data) {        return fail(ResultCode.unauthorized, msg, data);    }    public ActionResult unauthorized(String msg) {        return unauthorized(msg, null);    }}