package song.common.toolkit.base;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;/** * description: * author:          song * createDate:      2018/4/13 */public class IdModel<T> extends BaseModel {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    protected T id;    public T getId() {        return id;    }    public void setId(T id) {        this.id = id;    }}