package song.common.toolkit.export;import song.common.toolkit.excel.ExcelFactory;import song.common.toolkit.excel.WriteExcel;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * description:     excel导出器 * author:          song * createDate:      2018/5/8 */public class ExcelExporter extends Exporter {    public ExcelExporter(HttpServletResponse response) {        super(response);    }    @Override    public void export(ExportExcel excel) throws IOException {        this.setAttachment(excel.getFileName());        ExcelFactory.getWriter((WriteExcel) excel).write(this.response.getOutputStream());    }}