package song.common.toolkit.http;import org.springframework.web.servlet.config.annotation.CorsRegistry;/** * description: * author:          song * createDate:      2018/5/25 */public class CorsHelper {    public static void addAllow(CorsRegistry registry) {        registry.addMapping("/**")                .allowedOrigins("*")                .allowedMethods("GET", "POST", "PUT", "OPTIONS", "DELETE", "PATCH")                .allowCredentials(true).maxAge(3600);    }}