package song.common.toolkit.serializer;import com.alibaba.fastjson.JSON;import java.util.List;/** * description: * author:          song * createDate:      2017/10/19 */public class JSONSerializer implements IDataSerializer {    public String toString(Object object) {        return JSON.toJSONString(object);    }    public <T> T parseObject(String s, Class<T> tClass) {        return JSON.parseObject(s, tClass);    }    public <T> List<T> parseArray(String s, Class<T> tClass) {        return JSON.parseArray(s, tClass);    }}