package song.common.toolkit.task;import java.util.HashMap;import java.util.Map;/** * description: * author:          song * createDate:      2018/6/27 */public enum TaskHandleType {    httpService(0,"http服务"),    javaHandler(1, "java处理程序");    private int value;    private String text;    private static Map<Integer, TaskHandleType> enumMap = new HashMap<>();    TaskHandleType(int value, String text) {        this.value = value;        this.text = text;    }    static {        for (TaskHandleType status : TaskHandleType.values()) {            enumMap.put(status.getValue(), status);        }    }    public static TaskHandleType valueOf(int value) {        return enumMap.get(value);    }    public int getValue() {        return value;    }    public void setValue(int value) {        this.value = value;    }    public String getText() {        return text;    }    public void setText(String text) {        this.text = text;    }}