package song.common.toolkit.task;import java.util.HashMap;import java.util.Map;/** * description: * author:          song * createDate:      2018/6/27 */public enum TaskType {    importer(0,"导入"),    exporter(1, "导出");    private int value;    private String text;    private static Map<Integer, TaskType> enumMap = new HashMap<>();    TaskType(int value, String text) {        this.value = value;        this.text = text;    }    static {        for (TaskType status : TaskType.values()) {            enumMap.put(status.getValue(), status);        }    }    public static TaskType valueOf(int value) {        return enumMap.get(value);    }    public int getValue() {        return value;    }    public void setValue(int value) {        this.value = value;    }    public String getText() {        return text;    }    public void setText(String text) {        this.text = text;    }}