package song.common.algorithm;/** * description: * author:          song * createDate:      2018/6/11 */public class SortHelper {    /**     * 冒泡排序     * @param arrs     * @return     */    public static int[] bubble(int[] arrs) {        boolean isSort = false;        for (int i = 0; i < arrs.length - 1; i++) {            if (!isSort) {                isSort = true;                for (int j = 0; j < arrs.length - 1 - i; j++) {                    if (arrs[j] > arrs[j + 1]) {                        int temp = arrs[j];                        arrs[j] = arrs[j + 1];                        arrs[j + 1] = temp;                        isSort = false;                    }                }            } else {                break;            }        }        return arrs;    }}