package song.common.db;import song.common.lang.StringHelper;import java.util.ArrayList;import java.util.List;/** * description: * author:          song * createDate:      2017/10/25 */public enum DBType {    oracle("oracle"),    mysql("mysql"),    sqlserver("sqlserver"),    postgresql("postgresql"),    mongodb("mongodb"),    db2("db2"),    access("access"),    sqlite("sqlite"),    other("");    private String name;    DBType(String name) {        this.name = name;    }    /**     * 解析枚举值     *     * @param name     * @return     */    public static DBType parse(String name) {        DBType[] types = values();        for (DBType type : types) {            if (type.getName().equalsIgnoreCase(name)) {                return type;            }        }        return null;    }    public static List<String> getNames() {        DBType[] types = values();        ArrayList<String> list = new ArrayList<String>();        for (int i = 0; i < types.length; i++) {            String name = types[i].getName();            if (!StringHelper.isEmpty(name)) {                list.add(name);            }        }        return list;    }    public String getName() {        return name;    }    @Override    public String toString() {        return name;    }}