package song.common.io;/** * description: * author:          song * createDate:      2017/10/31 */public class  FileInfo {    private String fileName;    private String filePath;    private byte[] bytes;    private long size;    private String extension;    public String getFileName() {        return fileName;    }    public void setFileName(String fileName) {        this.fileName = fileName;    }    public String getExtension() {        return extension;    }    public void setExtension(String extension) {        this.extension = extension;    }    public long getSize() {        return size;    }    public void setSize(long size) {        this.size = size;    }    public String getFilePath() {        return filePath;    }    public void setFilePath(String filePath) {        this.filePath = filePath;    }    public byte[] getBytes() {        return bytes;    }    public void setBytes(byte[] bytes) {        this.bytes = bytes;    }}