package song.common.lang;/** * description: * author:          song * createDate:      2017/10/27 */public class ArrayHelper {    /**     * 判断字符串是否存在数组里     *     * @param s     * @param arrays     * @return     */    public static boolean contains(String s, String[]... arrays) {        for (String[] array : arrays) {            for (String item : array) {                if (item.equalsIgnoreCase(s)) {                    return true;                }            }        }        return false;    }}