package song.common.net;/** * description: * author:          song * createDate:      2018/5/30 */public class ServerConnection {    private String server;    private int port;    private String userName;    private String password;    public ServerConnection(String server, int port, String userName, String password) {        this.server = server;        this.port = port;        this.userName = userName;        this.password = password;    }    public ServerConnection() {    }    public String getServer() {        return server;    }    public void setServer(String server) {        this.server = server;    }    public int getPort() {        return port;    }    public void setPort(int port) {        this.port = port;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }}