package song.common.net.http;import song.common.io.FileHelper;import song.common.io.StreamHelper;import song.common.lang.CharsetHelper;import song.common.lang.ObjectHelper;import song.common.lang.StringHelper;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.io.OutputStream;/** * description:     HttpResponse * author:          song * createDate:      2017/9/27 */public class HttpResponse {    protected HttpServletResponse response;    public HttpResponse(HttpServletResponse response) {        this.response = response;    }    public HttpServletResponse getResponse() {        return response;    }    public HttpResponse setCharset(){        response.setCharacterEncoding(CharsetHelper.getDefault());        return this;    }    public HttpResponse setJsonType(){        response.setContentType("application/json; charset=utf-8");        return this;    }    public HttpResponse reset() {        this.response.reset();        return this;    }    public void write(String s) throws IOException {        write(StringHelper.getBytes(s));    }    /**     * 输入流中写入文件     *     * @param filePath     * @throws IOException     */    public void writeFile(String filePath) throws IOException {        write(FileHelper.getBytes(filePath));    }    /**     * 输入流中写入内容     *     * @param bytes     * @throws IOException     */    public void write(byte[] bytes) throws IOException {        OutputStream stream = null;        try {            stream = response.getOutputStream();            stream.write(bytes);        } finally {            StreamHelper.close(stream);        }    }}