package song.common.result;import java.util.ArrayList;import java.util.List;/** * description: * author:          song * createDate:      2018/4/24 */public class TreeResult {    private String id;    private String label;    private boolean disabled;    private boolean selected;    private boolean checked;    private boolean isLeaf;    private List<TreeResult> children;    public TreeResult() {    }    public TreeResult(String label) {        this.label = label;    }    public TreeResult(String id, String label) {        this.id = id;        this.label = label;    }    public void appendChild(TreeResult child){        if(children==null){            children = new ArrayList<TreeResult>();        }        children.add(child);    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getLabel() {        return label;    }    public void setLabel(String label) {        this.label = label;    }    public boolean isDisabled() {        return disabled;    }    public void setDisabled(boolean disabled) {        this.disabled = disabled;    }    public boolean isSelected() {        return selected;    }    public void setSelected(boolean selected) {        this.selected = selected;    }    public boolean isChecked() {        return checked;    }    public void setChecked(boolean checked) {        this.checked = checked;    }    public boolean isLeaf() {        return isLeaf;    }    public void setLeaf(boolean leaf) {        isLeaf = leaf;    }    public List<TreeResult> getChildren() {        return children;    }    public void setChildren(List<TreeResult> children) {        this.children = children;    }}