package song.common.security;/** * description: * author:          song * createDate:      2018/6/14 */public class SimpleUser {    private String userId;    private String account;    private String userName;    public String getUserId() {        return userId;    }    public void setUserId(String userId) {        this.userId = userId;    }    public String getAccount() {        return account;    }    public void setAccount(String account) {        this.account = account;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public SimpleUser() {    }    public SimpleUser(String userId) {        this.userId = userId;    }}