package song.common.ui.easyui;import song.common.lang.StringHelper;import song.common.ui.builder.HtmlBuilder;import song.common.util.MapHelper;import java.util.HashMap;/** * description:     EasyuiBuilder * author:          song * createDate:      2017/9/25 */public class EasyuiBuilder extends HtmlBuilder {    private HashMap<String, String> dataOptions = new HashMap<String, String>();    public EasyuiBuilder(String tagName) {        super(tagName);    }    public EasyuiBuilder option(String key, String value) {        dataOptions.put(key, StringHelper.wrap(value, "'"));        return this;    }    public EasyuiBuilder option(String key, Boolean value) {        dataOptions.put(key, value.toString());        return this;    }    public EasyuiBuilder option(String key, Number value) {        dataOptions.put(key, value.toString());        return this;    }    @Override    protected String getAttributes() {        if (dataOptions.size() > 0) {            this.attr("pager-options", getDataOptions());        }        return super.getAttributes();    }    private String getDataOptions() {        return MapHelper.join(dataOptions, ":", ",");    }}