package song.common.ui.table;import java.util.ArrayList;import java.util.List;/** * description:     控件列配置 * author:          song * createDate:      2018/5/8 */public class TableColumn {    private int index;    private String prop;    private String label;    private String dataType;    private int width;    private boolean fixed;    private String align;    private List<TableColumn> columns;    public int getIndex() {        return index;    }    public void setIndex(int index) {        this.index = index;    }    public String getProp() {        return prop;    }    public void setProp(String prop) {        this.prop = prop;    }    public String getLabel() {        return label;    }    public void setLabel(String label) {        this.label = label;    }    public String getDataType() {        return dataType;    }    public void setDataType(String dataType) {        this.dataType = dataType;    }    public int getWidth() {        return width;    }    public void setWidth(int width) {        this.width = width;    }    public boolean isFixed() {        return fixed;    }    public void setFixed(boolean fixed) {        this.fixed = fixed;    }    public String getAlign() {        return align;    }    public void setAlign(String align) {        this.align = align;    }    public List<TableColumn> getColumns() {        return columns;    }    public void setColumns(List<TableColumn> columns) {        this.columns = columns;    }    public void addColumn(TableColumn column){        if(this.columns==null){            this.columns=new ArrayList<TableColumn>();        }        this.columns.add(column);    }}