package song.common.ui.tree;/** * description: * author:          song * createDate:      2017/11/20 */public class TreeNode {    private String id;    private String title;    private boolean expand;    private boolean disabled;    private boolean checked;    public TreeNode() {    }    public TreeNode(String id, String title) {        this.id = id;        this.title = title;    }    public TreeNode(String id, String title, boolean expand) {        this.id = id;        this.title = title;        this.expand = expand;    }    public TreeNode(String id, String title, boolean expand, boolean disabled) {        this.id = id;        this.title = title;        this.expand = expand;        this.disabled = disabled;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public boolean isExpand() {        return expand;    }    public void setExpand(boolean expand) {        this.expand = expand;    }    public boolean isDisabled() {        return disabled;    }    public void setDisabled(boolean disabled) {        this.disabled = disabled;    }    public boolean isChecked() {        return checked;    }    public void setChecked(boolean checked) {        this.checked = checked;    }}