package song.common.util;import sun.misc.BASE64Decoder;import sun.misc.BASE64Encoder;import java.io.IOException;/** * description:     BASE64Helper * author:          song * createDate:      2017/9/25 */public class BASE64Helper {    /**     * 将byte[]进行base64编码     *     * @param bytes     * @return     */    public static String encode(byte[] bytes) {        return new BASE64Encoder().encode(bytes);    }    /**     * 将字符串进行base64解码     *     * @param base64Code     * @return     * @throws IOException     */    public static byte[] decode(String base64Code) throws IOException {        return new BASE64Decoder().decodeBuffer(base64Code);    }}