package song.common.util;import song.common.lang.StringHelper;import java.text.SimpleDateFormat;import java.util.Date;/** * description:     DateHelper * author:          song * createDate:      2017/9/25 */public class DateHelper {    public static String format(Date date, String pattern) {        if (null != date) {            SimpleDateFormat dateFormat = new SimpleDateFormat(pattern);            dateFormat.format(date);        }        return StringHelper.getEmpty();    }    public static String now(String pattern) {        return format(new Date(), pattern);    }    public static String nowDate() {        return now(FormatHelper.date);    }    public static String nowDateTime() {        return now(FormatHelper.datetime);    }}