package song.common.util;import song.common.lang.StringHelper;import java.util.Random;/** * description:     RandomText * author:          song * createDate:      2017/9/25 */public class RandomText {    private boolean allowNumber = true;    private boolean allowLowerCase = false;    private boolean allowUpperCase = false;    private boolean allowSpecial = false;    private String customString = "";    private boolean filterConfusion = false;    public void setAllowNumber(boolean allowNumber) {        this.allowNumber = allowNumber;    }    public void setAllowLowerCase(boolean allowLowerCase) {        this.allowLowerCase = allowLowerCase;    }    public void setAllowUpperCase(boolean allowUpperCase) {        this.allowUpperCase = allowUpperCase;    }    public void setAllowSpecial(boolean allowSpecial) {        this.allowSpecial = allowSpecial;    }    public void setCustomString(String customString) {        this.customString = customString;    }    public void setFilterConfusion(boolean filterConfusion) {        this.filterConfusion = filterConfusion;    }    public String next(int length) {        String s = this.customString;        if (allowNumber) {            s += "0123456789";        }        if (allowLowerCase) {            s += "abcdefghijklmnopqrstuvwxyz";        }        if (allowUpperCase) {            s += "ABCDEFGHIJKLMNOPQRSTUVWXYZ";        }        if (allowSpecial) {            s += "!\\\"#$%&'()*+,-./:;<=>?@[\\\\]^_`{|}~";        }        if (filterConfusion) {            s = StringHelper.replace(s, "", "0", "o", "O");        }        String text = "";        Random random = new Random();        if (!StringHelper.isEmpty(s)) {            for (int i = 0; i < length; i++) {                int index = random.nextInt(s.length());                text += StringHelper.substring(s, index, 1);            }        }        return text;    }    public String next() {        return next(1);    }}