package song.common.util;import java.util.UUID;/** * description:     UUIDHelper * author:          song * createDate:      2017/9/25 */public class UUIDHelper {    public static String next(boolean isSeparator) {        String uuid = UUID.randomUUID().toString();        return isSeparator ? uuid : uuid.replaceAll("-", "");    }    public static String next() {        return next(false);    }    public static String nextNumber() {        String uuid = next();        return uuid.replaceAll("a", "1").replaceAll("b", "2").replaceAll("c", "3").replaceAll("d", "4").replaceAll("e", "5").replaceAll("f", "6");    }}