package algorithm.sort;import song.common.algorithm.SortHelper;/** * description: * author:          song * createDate:      2018/6/1 */public class BubbleSort {    /**     * 冒泡排序     * @param args     */    public static void main(String[] args) {        int[] arrs = {10, 20, 5, 233, 2, 11};        arrs = SortHelper.bubble(arrs);        for (int n = 0; n < arrs.length; n++) {            System.out.println(arrs[n]);        }    }}