package algorithm.sort;/** * description: * author:          song * createDate:      2018/6/1 */public class BubbleSort {    /**     * 冒泡排序     * @param args     */    public static void main(String[] args) {        int[] arrs = {10, 20, 5, 233, 2, 11};        boolean isSort = false;        for (int i = 0; i < arrs.length - 1; i++) {            if (!isSort) {                isSort = true;                for (int j = 0; j < arrs.length -1- i; j++) {                    if (arrs[j] > arrs[j + 1]) {                        int temp = arrs[j];                        arrs[j] = arrs[j + 1];                        arrs[j + 1] = temp;                        isSort = false;                    }                }            } else {                break;            }        }        for (int n = 0; n < arrs.length; n++) {            System.out.println(arrs[n]);        }    }}