package com.song.db;/** * description: * author:          song * createDate:      2017/10/26 */public class DBConnectionConfig {    private DBType dbType;    private String driver;    private String url;    private String userName;    private String password;    public DBConnectionConfig(DBType dbType, String url) {        this.dbType = dbType;    }    public DBConnectionConfig(DBType dbType, String url, String userName, String password) {        this.dbType = dbType;        this.url = url;        this.userName = userName;        this.password = password;    }    public DBType getDbType() {        return dbType;    }    public void setDbType(DBType dbType) {        this.dbType = dbType;    }    public String getDriver() {        return driver;    }    public void setDriver(String driver) {        this.driver = driver;    }    public String getUrl() {        return url;    }    public void setUrl(String url) {        this.url = url;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }}