package com.song.io;import java.io.IOException;import java.io.Writer;/** * description: * author:          song * createDate:      2017/10/19 */public class WriterHelper {    /**     * 关闭写入器     * @param writer     * @throws IOException     */    public static void close(Writer writer){        if(writer!=null){            try {                writer.flush();                writer.close();            } catch (IOException e) {                e.printStackTrace();            }        }    }}