package com.studio.builder.engine;import com.song.lang.StringHelper;/** * description: * author:          song * createDate:      2017/11/2 */public class CodeHelper {    /**     * 剔除字符串指定前缀     *     * @param s     * @param prefix     * @return     */    public static String trimPrefix(String s, String prefix) {        if (!StringHelper.isEmpty(prefix)) {            String[] prefixs = prefix.split(",");            for (String fix : prefixs) {                if (s.startsWith(fix)) {                    return StringHelper.trimStart(s, fix);                }            }        }        return s;    }}